var jsyaml = (function() {
    'use strict';

    /**
     * Simple YAML parser for basic key-value and nested structures
     * Note: This is a minimal implementation. For full YAML support, use the actual js-yaml library.
     */
    function load(yamlString) {
        if (!yamlString || typeof yamlString !== 'string') {
            return null;
        }

        const lines = yamlString.split('\n');
        const result = {};
        const stack = [{ indent: -1, obj: result }];

        for (let i = 0; i < lines.length; i++) {
            const line = lines[i];

            // Skip empty lines and comments
            if (!line.trim() || line.trim().startsWith('#')) {
                continue;
            }

            // Calculate indentation
            const indent = line.search(/\S/);
            const content = line.trim();

            // Skip if only whitespace
            if (indent === -1) continue;

            // Pop stack until we find the right parent
            while (stack.length > 1 && stack[stack.length - 1].indent >= indent) {
                stack.pop();
            }

            const parent = stack[stack.length - 1].obj;

            // Parse the line
            if (content.includes(':')) {
                const colonIndex = content.indexOf(':');
                const key = content.substring(0, colonIndex).trim();
                let value = content.substring(colonIndex + 1).trim();

                if (value === '' || value === '|' || value === '>') {
                    // Nested object or multiline string
                    parent[key] = {};
                    stack.push({ indent: indent, obj: parent[key] });
                } else if (value.startsWith('[') && value.endsWith(']')) {
                    // Inline array
                    parent[key] = parseInlineArray(value);
                } else if (value.startsWith('{') && value.endsWith('}')) {
                    // Inline object
                    parent[key] = parseInlineObject(value);
                } else {
                    // Simple value
                    parent[key] = parseValue(value);
                }
            } else if (content.startsWith('- ')) {
                // Array item
                const value = content.substring(2).trim();

                // Find the array in parent or create one
                const parentKeys = Object.keys(parent);
                if (parentKeys.length > 0) {
                    const lastKey = parentKeys[parentKeys.length - 1];
                    if (!Array.isArray(parent[lastKey])) {
                        parent[lastKey] = [];
                    }

                    if (value.includes(':')) {
                        // Object in array
                        const obj = {};
                        const colonIndex = value.indexOf(':');
                        const k = value.substring(0, colonIndex).trim();
                        const v = value.substring(colonIndex + 1).trim();
                        obj[k] = parseValue(v);
                        parent[lastKey].push(obj);
                    } else {
                        parent[lastKey].push(parseValue(value));
                    }
                }
            }
        }

        return result;
    }

    function parseValue(str) {
        if (str === 'true') return true;
        if (str === 'false') return false;
        if (str === 'null' || str === '~') return null;

        // Remove quotes
        if ((str.startsWith('"') && str.endsWith('"')) ||
            (str.startsWith("'") && str.endsWith("'"))) {
            return str.slice(1, -1);
        }

        // Try to parse as number
        const num = Number(str);
        if (!isNaN(num) && str !== '') {
            return num;
        }

        return str;
    }

    function parseInlineArray(str) {
        // Remove brackets
        const inner = str.slice(1, -1).trim();
        if (!inner) return [];

        // Split by comma (simple implementation)
        return inner.split(',').map(item => parseValue(item.trim()));
    }

    function parseInlineObject(str) {
        // Remove braces
        const inner = str.slice(1, -1).trim();
        if (!inner) return {};

        const obj = {};
        const pairs = inner.split(',');

        for (const pair of pairs) {
            const colonIndex = pair.indexOf(':');
            if (colonIndex !== -1) {
                const key = pair.substring(0, colonIndex).trim();
                const value = pair.substring(colonIndex + 1).trim();
                obj[key] = parseValue(value);
            }
        }

        return obj;
    }

    function dump(obj, indent = 0) {
        const spaces = '  '.repeat(indent);
        let result = '';

        if (Array.isArray(obj)) {
            for (const item of obj) {
                if (typeof item === 'object' && item !== null) {
                    result += spaces + '-\n' + dump(item, indent + 1);
                } else {
                    result += spaces + '- ' + String(item) + '\n';
                }
            }
        } else if (typeof obj === 'object' && obj !== null) {
            for (const [key, value] of Object.entries(obj)) {
                if (typeof value === 'object' && value !== null) {
                    result += spaces + key + ':\n' + dump(value, indent + 1);
                } else {
                    result += spaces + key + ': ' + String(value) + '\n';
                }
            }
        } else {
            result = String(obj);
        }

        return result;
    }

    return {
        load: load,
        dump: dump,
        safeLoad: load,  // Alias for compatibility
        safeDump: dump   // Alias for compatibility
    };
})();

